import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { getBraintreeClientToken } from '../../actions/braintreeActions';
import DropIn from "braintree-web-drop-in-react";
import './PaymentOptions.css';

const PaymentOptions = ({reRoute}) => {
  const dispatch = useDispatch();
  const cart = useSelector(state => state.cart);
  const clientToken = useSelector(state => state.clientToken)
  const { email, state, country, city, address, postalCode } = cart.checkoutInfo;

  const [data, setData] = useState({
    success: false,
    clientToken: null,
    error: '',
    instance: {},
    address: ''
  })

  console.log(data);

  useEffect(() => {
    dispatch(getBraintreeClientToken())
    setData({...data, clientToken: clientToken, success: true})
  },[])

  const showDropIn = () => {
    return (
      <div>
        { data.clientToken !== null && cart.cartItems.length > 0 ? (
          <div>
            <DropIn
              options={{ authorization: data.clientToken }}
              onInstance={(instance) => (data.instance = instance)}
            />
            <button>Checkout</button>
          </div>
        ) : null }
      </div>
    )
  }
  return(
    <div>
      {showDropIn()}
    </div>

  )
}

export default PaymentOptions;
