import React, {useState, useEffect} from 'react';
import {useDispatch, useSelector } from 'react-redux';
import { listProducts } from '../../actions/productActions';
import './AddProductScreen.css';

const AddProductScreen = () => {
  const [id, setId] = useState('');
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');
  const [category, setCategory] = useState('');
  const [countInStock, setCountInStock] = useState({});

  const productList = useSelector(state=>state.productList);
  const { loading, products, error } = productList;

  console.log(products);


  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(listProducts());
    return () => {

    };
  }, [])

  const submitHandler = () => {

  }

  return(
    <div className='add-product-screen'>
      {products &&
        <div className='product-list'>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Price</th>
                <th>Category</th>
                <th>Count In Stock</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {

                products.map((product) => (
                <tr key={product._id}>
                  <td>{product._id}</td>
                  <td>{product.name}</td>
                  <td>{product.category}</td>
                  <td>{product.price}</td>
                  <td>{product.countInStock['small']}</td>
                  <td>
                    <button>Edit</button>
                    <button >Delete</button>
                  </td>

                </tr>
              ))}

            </tbody>
          </table>
        </div>
    }
    <div className='add-product-action'>
      <form onSubmit={submitHandler}>
        <ul>
          <li>
            <legend>Create Product</legend>
          </li>
          <li>
            <label htmlFor='name'>Name</label>
            <input type='text' name='name' value={name} id='name' onChange={(e) => setName(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='description'>Description</label>
            <textarea name='description' value={description} id='description' onChange={(e) => setDescription(e.target.value)}></textarea>
          </li>
          <li>
            <label htmlFor='price'>Price</label>
            <input type='Number' name='price' value={price} id='price' onChange={(e) => setPrice(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='image'>Image</label>
            <input type='text' name='image' value={image} id='image' onChange={(e) => setImage(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='category'>Category</label>
            <input type='text' name='category' value={category} id='category' onChange={(e) => setCategory(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='countInStock'>Count In Stock</label>
            <input className='size-box' type='Number' name='countInStock' value={countInStock} id='countInStock' onChange={(e) => setCountInStock(e.target.value)}></input>
          </li>
          <li>
            <button type='submit' className='button-primary' onClick={submitHandler}>{id ? 'Update' : 'Create'}</button>
          </li>
          <li>
            <button type='submit' className='button-secondary'>Back</button>
          </li>
        </ul>
      </form>
    </div>
      </div>
  )
}

export default AddProductScreen;
