import React from 'react';
import './CartItem.css';
import { useDispatch } from 'react-redux';
import { addToCart } from '../../actions/cartActions';

const CartItem = ({cartItem, edit}) => {
  const dispatch = useDispatch();


  return (
    <div className='cart-item'>
      <div className='display'>
        <img src={require(`../../assets/${cartItem.image}`)} alt={cartItem.name} />
        <div className='info'>
          <h3>{cartItem.name}</h3>
          <p>{cartItem.description}</p>
          <p className='info-tag'>Size: {cartItem.size}</p>
          <p className='info-tag'>Color: {cartItem.color}</p>
        </div>
      </div>
      <div className='price'>${cartItem.price}</div>
    </div>
  )
}

export default CartItem;
