import React, { useEffect, useState }  from 'react';
import {Link} from 'react-router-dom';
import {useSelector, useDispatch} from 'react-redux';
import { saveInformation } from '../../actions/cartActions';
import InputBox from './InputBox/InputBox';
import './InformationForm.css';
import {formData} from './formData';


const InformationForm = ({reRoute}) => {
  const cart = useSelector(state => state.cart);
  const [checkoutForm, setCheckoutForm] = useState({});
  const [email, setEmail] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const [country, setCountry] = useState('');
  const [state, setState] = useState('');
  const dispatch = useDispatch();

  useEffect(() => {
    setCheckoutForm({
      ...cart.checkoutInfo
    })
  },[])
  const handleChange = name => event => {
      const value = event.target.value;

      setCheckoutForm({...checkoutForm, [name]: value});
  }

  const submitHandler = (e) =>{
    e.preventDefault();
    if(!country === 'United States'){
      setState('');
    }
    dispatch(saveInformation({email, firstName, lastName, address, city, postalCode, country, state}));
    reRoute('shipping');
  }

  return(
    <div className='information-form'>
      <form onSubmit={submitHandler}>
        <ul>
          <li>
            <legend>Contact Info</legend>
          </li>
          <li>
            <div className='input-box'>
              <label htmlFor='email'>Email</label>
              <input type='input' name='email' id='email' value={checkoutForm.email} required={true} onChange={(e) => handleChange(e.target.value)} ></input>
            </div>
          </li>
          <li>
            <legend>Shipping</legend>
          </li>
          <li>
            <div className='li-name'>
              <div className='input-box'>
                <label htmlFor='firstName'>First Name</label>
                <input type='input' name='firstName' id='email' value={checkoutForm.firstName} required={true} onChange={(e) => setFirstName(e.target.value)} ></input>
              </div>
              <div className='input-box'>
                <label htmlFor='email'>Last Name</label>
                <input type='input' name='lastName' id='email' value={checkoutForm.lastName} required={true} onChange={(e) => setLastName(e.target.value)} ></input>
              </div>
            </div>
          </li>
          <li>
            <div className='input-box'>
              <label htmlFor='address'>Address</label>
              <input type='input' name='address' id='address' value={checkoutForm.address} required={true} onChange={(e) => setAddress(e.target.value)} ></input>
            </div>
          </li>
          <li>
            <div className='input-box'>
              <label htmlFor='city'>City</label>
              <input type='input' name='city' id='city' value={checkoutForm.city} required={true} onChange={(e) => setCity(e.target.value)} ></input>
            </div>
          </li>
          <li>
            <div className='input-box'>
              <label htmlFor='postalCode'>Postal Code</label>
              <input type='input' name='postalCode' id='postalCode' value={checkoutForm.postalCode} required={true} onChange={(e) => setPostalCode(e.target.value)} ></input>
            </div>
          </li>
          <li>
            <div className='input-box'>
              <label>Country</label>
              <select className='select-box' value={checkoutForm.country} onChange={(e) => { setCountry(e.target.value)}}>
                <option></option>
                {formData.countries.map((c, i) => {
                  return <option key={i} value={c}>{c}</option>
                })

                }
              </select>
            </div>
          </li>
          { country === 'United States' &&
            <li>
            <div className='input-box'>
              <label>State</label>
              <select className='select-box' onChange={(e) => { setState(e.target.value)}}>
                <option></option>
                {formData.states.map((s, i) => {
                  return <option key={i} value={s}>{s}</option>
                })

                }
              </select>
            </div>
          </li>
          }
          <li className='li-information-actions'>
            <Link to='/cart' className='link-previous'>{`< Back to Cart`}</Link>
            <button type='submit' className='button-primary'>Continue to shipping</button>
          </li>
        </ul>
      </form>
    </div>
  )
}

export default InformationForm;
