import React, { useState, useEffect } from 'react';
import queryString from 'query-string';
import { useSelector } from 'react-redux';
import InformationForm from '../InformationForm/InformationForm';
import CheckoutSteps from '../CheckoutSteps/CheckoutSteps';
import ShippingOptions from '../ShippingOptions/ShippingOptions';
import CartItem from '../CartItem/CartItem';
import CheckoutItemCard from '../CheckoutItemCard/CheckoutItemCard';
import './CheckoutScreen.css';

const CheckoutScreen = (props) => {
  const cart = useSelector(state => state.cart);
  const { cartItems } = cart;
  const [subTotal, setSubTotal] = useState(cartItems.reduce((a, c) =>  a + c.price * c.qty, 0));
  const [tax, setTax] = useState(0);
  const [shipping, setShipping] = useState(0);

  const reRoute = (route) => {
    if(route === 'shipping'){
      props.history.push('?step=shipping');
    } else if(route === 'cart'){
      props.history.push('/cart');
    } else if (route === 'information'){
      props.history.push('?step=information');
    } else if (route === 'payment'){
      props.history.push('?step=payment');
    } else {
      props.history.push('/');
    }
  }

  const params = queryString.parse(props.location.search);

  useEffect(() => {
    if(cart.checkoutInfo){
      cart.checkoutInfo.state === 'California' ? setTax(subTotal * 0.095) : setTax(0);
    }
  },[])

  return(
    <div className='checkout-screen'>
      <div className='checkout-action'>
        <h3>Check out</h3>
        <CheckoutSteps step1 step2 step3 reRoute={reRoute}/>
        {
          params.step === 'shipping'
          ?
            <ShippingOptions reRoute={reRoute}/>
          : <InformationForm reRoute={reRoute} />
        }

      </div>
      <div className='checkout-info'>
        <h3>Order Info</h3>
        <div className='checkout-items-list'>
          {
          cartItems.length === 0 ?
            <div> Cart is Empty.</div> :
            cartItems.map((cartItem, i) =>
              <CheckoutItemCard item={cartItem} key={i}  />
            )
          }
        </div>
        <div className='checkout-subtotal'>
          <div className='space-between'>
            <p>Subtotal</p>
            <p>${subTotal}</p>
          </div>
          <div className='space-between'>
            <p>Tax</p>
            <p>${tax}</p>
          </div>
          <div className='space-between'>
            <p>Shipping</p>
            <p>Calculated at next step</p>
          </div>
        </div>
        <div className='checkout-total'>
          <div className='space-between'>
            <p>Total</p>
            <p>USD ${subTotal + shipping + tax}</p>
          </div>
        </div>

      </div>


    </div>
  );
}

export default CheckoutScreen;
