import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import InformationForm from '../InformationForm/InformationForm';
import CheckoutSteps from '../CheckoutSteps/CheckoutSteps';
import CartItem from '../CartItem/CartItem';
import CheckoutItemCard from '../CheckoutItemCard/CheckoutItemCard';
import './CheckoutScreen.css';

const CheckoutScreen = (props) => {
  const cart = useSelector(state => state.cart);
  const [email, setEmail] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const [country, setCountry] = useState('');
  const [state, setState] = useState('');
  const dispatch = useDispatch();
  const { cartItems } = cart;
  const [subTotal, setSubTotal] = useState(cartItems.reduce((a, c) =>  a + c.price * c.qty, 0));
  const [tax, setTax] = useState(0);
  const [shipping, setShipping] = useState(0);
  const [total, setTotal] = useState(subTotal + tax + shipping);

  const submitHandler = (e) =>{
    props.history.push('/checkout?step=shipping');
    e.preventDefault();
    dispatch(saveInformation({email, firstName, lastName, address, city, postalCode, country, state}));
  }

  useEffect(() => {
    if(state === 'California'){
      setTax(subTotal* 0.095);
    } else {
      setTax(0);
    }
  },[state])

  return(
    <div className='checkout-screen'>
      <div className='checkout-action'>
        <h3>Check out</h3>
        <CheckoutSteps step1 step2/>
        <InformationForm></InformationForm>
      </div>
      <div className='checkout-info'>
        <h3>Order Info</h3>
        <div className='checkout-items-list'>
          {
          cartItems.length === 0 ?
            <div> Cart is Empty.</div> :
            cartItems.map((cartItem, i) =>
              <CheckoutItemCard item={cartItem} key={i}  />
            )
          }
        </div>
        <div className='checkout-subtotal'>
          <div className='space-between'>
            <p>Subtotal</p>
            <p>${subTotal}</p>
          </div>
          <div className='space-between'>
            <p>Tax</p>
            <p>${tax}</p>
          </div>
          <div className='space-between'>
            <p>Shipping</p>
            <p>Calculated at next step</p>
          </div>
        </div>
        <div className='checkout-total'>
          <div className='space-between'>
            <p>Total</p>
            <p>USD ${total}</p>
          </div>
        </div>

      </div>


    </div>
  );
}

export default CheckoutScreen;
