import React, { useEffect, useState }  from 'react';
import {Link} from 'react-router-dom';
import {useSelector, useDispatch} from 'react-redux';
import InputBox from './InputBox/InputBox';
import './InformationForm.css';
import {formData} from './formData';


const InformationForm = (props) => {
  const [email, setEmail] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const [country, setCountry] = useState('');
  const dispatch = useDispatch();

  const submitHandler = (e) =>{
    e.preventDefault();
    props.history.push('payment');
  }
  return(
    <div className='shipping-form'>
      <form onSubmit={submitHandler}>
        <ul>
          <li>
            <legend>Contact Info</legend>
          </li>
          <li>
            <InputBox type='email' name='email' text={'Email'} />
          </li>
          <li>
            <legend>Shipping</legend>
          </li>
          <li>
            <div className='li-name'>
              <div>
                <InputBox type='text' name='firstName' text={'First Name'} />
              </div>
              <div>
                <InputBox type='text' name='lastName' text={'Last Name'} />
              </div>
            </div>
          </li>
          <li>
            <InputBox type='text' name='address' text={'Address'} />
          </li>
          <li>
            <InputBox type='text' name='city' text={'City'} />
          </li>
          <li>
            <InputBox type='text' name='postalCode' text={'Postal Code'} />
          </li>
          <li>
            <select>
              {formData.countries.map((country) => {
                return <option>country</option>
              })

              }
            </select>
          </li>
          <li className='li-information-actions'>
            <Link to='/cart' className='link-previous'>{`< Back to Cart`}</Link>
            <button type='submit' className='button-primary'>Continue to shipping</button>
          </li>
        </ul>
      </form>
    </div>
  )
}

export default InformationForm;
