import React from 'react';
import './CartItem.css';
import { useDispatch } from 'react-redux';
import { addToCart } from '../../actions/cartActions';

const CartItem = ({cartItem, edit}) => {
  const dispatch = useDispatch();
  const removeFromCartHandler = (productId) => {
    if(productId) {
      dispatch(removeFromCart(productId));
    }
  }

  return (
    <div className='cart-item'>
      <div className='display'>
        <img src={require(`../../assets/${cartItem.image}`)} alt={cartItem.name} width='100px' />
        <div className='info'>
          <h3>
            {cartItem.name}
          </h3>
          {
            edit ?
            <div>
              <select value={cartItem.qty} onChange={(e) => dispatch(addToCart(cartItem.product, e.target.value))}>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='3'>3</option>
              </select>
              <button type='button' onClick={() => removeFromCartHandler(cartItem.product)}>Delete</button>
            </div>
            :
            <div>
                Qty: {cartItem.qty}
            </div>
          }


        </div>
      </div>
      <div className='price'>${cartItem.price}</div>
    </div>
  )
}

export default CartItem;
