import React, {useState, useEffect} from 'react';
import {useDispatch, useSelector } from 'react-redux';
import { listProducts, deleteProduct, saveProduct } from '../../actions/productActions';
import './AddProductScreen.css';

const AddProductScreen = () => {
  const [id, setId] = useState('');
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');
  const [category, setCategory] = useState('');
  const [small, setSmall] = useState('');
  const [medium, setMedium] = useState('');
  const [large, setLarge] = useState('');

  const productList = useSelector(state=>state.productList);
  const { loading, products, error } = productList;

  const productSave = useSelector(state=>state.productSave);
  const { loading: loadingSave, success: successSave, error: errorSave } = productSave;

  const productDelete = useSelector(state=>state.productDelete);
  const { loading: loadingDelete, success: successDelete, error: errorDelete } = productDelete;

  const dispatch = useDispatch();
  if(product){
    console.log(product.countInStock);
  }
  useEffect(() => {
    dispatch(listProducts());
    return () => {

    };
  }, [successSave, successDelete])

  const submitHandler = (e) => {
    e.preventDefault();
    const countInStock = {
      small: small,
      medium: medium,
      large: large
    }
    dispatch(saveProduct({
      _id: id,
        name, description, price, image, category, countInStock
    }));

    Array.from(document.querySelectorAll("input")).forEach(
      input => (input.value = "")
);

  }

  const deleteHandler = (product) => {
    dispatch(deleteProduct(product._id));
  }

  return(
    <div className='add-product-screen'>
      {products &&
        <div className='product-list'>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Price</th>
                <th>Category</th>
                <th>Count In Stock</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {

                products.map((product) => (
                <tr key={product._id}>
                  <td>{product._id}</td>
                  <td>{product.name}</td>
                  <td>{product.category}</td>
                  <td>{product.price}</td>
                  <td></td>
                  <td>
                    <button>Edit</button>
                    <button onClick={() => deleteHandler(product)}>Delete</button>
                  </td>

                </tr>
              ))}

            </tbody>
          </table>
        </div>
    }
    <div className='add-product-action'>
      <form onSubmit={submitHandler}>
        <ul>
          <li>
            <legend>Create Product</legend>
          </li>
          <li>
            <label htmlFor='name'>Name</label>
            <input type='text' name='name' value={name} id='name' onChange={(e) => setName(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='description'>Description</label>
            <textarea name='description' value={description} id='description' onChange={(e) => setDescription(e.target.value)}></textarea>
          </li>
          <li>
            <label htmlFor='price'>Price</label>
            <input type='Number' name='price' value={price} id='price' onChange={(e) => setPrice(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='image'>Image</label>
            <input type='text' name='image' value={image} id='image' onChange={(e) => setImage(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='category'>Category</label>
            <input type='text' name='category' value={category} id='category' onChange={(e) => setCategory(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='countInStock'>Count In Stock</label>
            <table>
              <thead className='size-header'>
                <tr><td>Small</td><td>Medium</td><td>Large</td></tr>
              </thead>
              <tbody>
                <tr>
                  <td><input className='size-box' type='Number' name='small' value={small} id='small' onChange={(e) => setSmall(e.target.value)}></input></td>
                  <td><input className='size-box' type='Number' name='medium' value={medium} id='medium' onChange={(e) => setMedium(e.target.value)}></input></td>
                  <td><input className='size-box' type='Number' name='large' value={large} id='large' onChange={(e) => setLarge(e.target.value)}></input></td>
                </tr>
              </tbody>
            </table>
          </li>
          <li>
            <button type='submit' className='button-primary' onClick={submitHandler}>{id ? 'Update' : 'Create'}</button>
          </li>
          <li>
            <button type='submit' className='button-secondary'>Back</button>
          </li>
        </ul>
      </form>
    </div>
      </div>
  )
}

export default AddProductScreen;
