import React, { useEffect, useState }  from 'react';
import {Link} from 'react-router-dom';
import {useSelector, useDispatch} from 'react-redux';
import {register} from '../../actions/userActions';
import './RegisterScreen.css';


const RegisterScreen = (props) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [rePassword, setRePassword] = useState('');
  const userRegister = useSelector(state=>state.userRegister);
  const { loading, userInfo, error } = userRegister;
  const dispatch = useDispatch();
  const redirect = props.location.search?props.location.search.split('=')[1]:'/';

  useEffect(() => {

    return () => {

    };
  }, [userInfo])

  const submitHandler = (e) =>{
    e.preventDefault();
    dispatch(register(name, email, password));
  }
  return(
    <div className='register-screen'>
      <form onSubmit={submitHandler}>
        <ul>
          <li>
            <legend>Register</legend>
          </li>
          <li>
            <label htmlFor='name'>Name</label>
            <input type='name' name='name' id='name' onChange={(e) => setName(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='email'>Email</label>
            <input type='email' name='email' id='email' onChange={(e) => setEmail(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='password'>Password</label>
            <input type='password' name='password' id='password' onChange={(e) => setPassword(e.target.value)}></input>
          </li>
          <li>
            <label htmlFor='rePassword'>Confirm Password</label>
            <input type='password' name='rePassword' id='rePassword' onChange={(e) => setRePassword(e.target.value)}></input>
          </li>
          <li>
            <button type='submit' className='button-primary'>Create Account</button>
          </li>
          <li>
            <Link to={redirect === '/' ? 'signin' : 'signin?redirect=' + redirect} className='button-register'> Signin </Link>
          </li>
        </ul>
      </form>
    </div>
  )
}

export default RegisterScreen;
