import React, { useEffect, useState }  from 'react';
import {Link} from 'react-router-dom';
import {useSelector, useDispatch} from 'react-redux';
import { saveInformation } from '../../actions/cartActions';
import InputBox from './InputBox/InputBox';
import './InformationForm.css';
import {formData} from './formData';


const InformationForm = ({reRoute}) => {
  const [email, setEmail] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const [country, setCountry] = useState('');
  const [state, setState] = useState('');
  const dispatch = useDispatch();


  const submitHandler = (e) =>{
    e.preventDefault();
    dispatch(saveInformation({email, firstName, lastName, address, city, postalCode, country, state}));
    reRoute();
  }

  return(
    <div className='information-form'>
      <form onSubmit={submitHandler}>
        <ul>
          <li>
            <legend>Contact Info</legend>
          </li>
          <li>
            <div className='input-box'>
              <label htmlFor='email'>{text}</label>
              <input type='input' name='email' id='email' required={true} ></input>
            </div>
          </li>
          <li>
            <legend>Shipping</legend>
          </li>
          <li>
            <div className='li-name'>
              <div>
                <InputBox type='text' name='firstName' text={'First Name'}/>
              </div>
              <div>
                <InputBox type='text' name='lastName' text={'Last Name'}/>
              </div>
            </div>
          </li>
          <li>
            <InputBox type='text' name='address' text={'Address'} />
          </li>
          <li>
            <InputBox type='text' name='city' text={'City'} />
          </li>
          <li>
            <InputBox type='text' name='postalCode' text={'Postal Code'} />
          </li>
          <li>
            <div className='input-box'>
              <label>Country</label>
              <select className='select-box' onChange={(e) => { setCountry(e.target.value)}}>
                <option></option>
                {formData.countries.map((c, i) => {
                  return <option key={i} value={c}>{c}</option>
                })

                }
              </select>
            </div>
          </li>
          { country === 'United States of America' &&
            <li>
            <div className='input-box'>
              <label>State</label>
              <select className='select-box' onChange={(e) => { setState(e.target.value)}}>
                <option></option>
                {formData.states.map((s, i) => {
                  return <option key={i} value={s}>{s}</option>
                })

                }
              </select>
            </div>
          </li>
          }
          <li className='li-information-actions'>
            <Link to='/cart' className='link-previous'>{`< Back to Cart`}</Link>
            <button type='submit' className='button-primary'>Continue to shipping</button>
          </li>
        </ul>
      </form>
    </div>
  )
}

export default InformationForm;
