import { PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS, PRODUCT_LIST_FAIL,
          PRODUCT_INFO_REQUEST, PRODUCT_INFO_SUCCESS, PRODUCT_INFO_FAIL} from '../constants/productConstants';

import axios from 'axios';

const listProducts = () => async (dispatch) => {
  try{
    dispatch({ type: PRODUCT_LIST_REQUEST });
    const { data } = await axios.get('/api/products');
    dispatch({type: PRODUCT_LIST_SUCCESS, payload: data});
  }
  catch(error){
    dispatch({ type: PRODUCT_LIST_FAIL, payload: error.message });
  }
}

const infoProduct = (productId) => async (dispatch) => {
  try{
    dispatch({type: PRODUCT_INFO_REQUEST});
    const { data } = await axios.get('/api/products/' + productId);
    dispatch({type: PRODUCT_INFO_SUCCESS, payload: data});
  } catch(error){
    dispatch({type: PRODUCT_INFO_FAIL, payload: error.message});
  }
}

const deleteProduct = (productId) => async (dispatch) => {
  try{
    const { userSignin: { userInfo } } = getState();
    dispatch({type: PRODUCT_DELETE_REQUEST});
  } catch (error) {
    return null;
  }
}
export { listProducts, infoProduct, deleteProduct };
