import axios from 'axios';
import Cookie from 'js-cookie';
import { CART_ADD_ITEM, CART_REMOVE_ITEM, CART_SAVE_INFORMATION, CART_SAVE_PAYMENT } from '../constants/cartConstants';

const addToCart = (productId, size, qty) => async (dispatch, getState) => {
  try{
    const { data } = await axios.get('/api/products/' + productId);
    dispatch({type: CART_ADD_ITEM, payload:{
      product: data._id,
      name: data.name,
      price: data.price,
      countInStock: data.countInStock[size],
      size: size,
      qty: parseInt(qty,10),
    }});

    const {cart:{cartItems}} = getState();
    Cookie.set('cartItems', JSON.stringify(cartItems));
  } catch(error){

  }
}
const removeFromCart = (productId) => async (dispatch, getState) => {
  dispatch({ type: CART_REMOVE_ITEM, payload: {product:productId} });

  const {cart:{cartItems}} = getState();
  Cookie.set('cartItems', JSON.stringify(cartItems));

}

const saveInformation = (data) => (dispatch, getState) => {
  dispatch({ type: CART_SAVE_INFORMATION, payload: data });
  console.log(data);
  const {cart:{information}} = getState();
  Cookie.set('checkoutInfo', JSON.stringify(information));
}
const savePayment = (data) => (dispatch) => {
  dispatch({ type: CART_SAVE_PAYMENT, payload: data });
}
export { addToCart, removeFromCart, saveInformation, savePayment }
