import React from 'react';
import './InputBox.css';

const InputBox = ({type, name, text, value, handleChange}) => {
  return(
    <div className='input-box'>
      <label htmlFor={name}>{text}</label>
      <input type={type} name={name} id={name} required={true} value={value} onChange={() => handleChange.handleChange(name)}></input>
    </div>
  )
}

export default InputBox;
